export default function Products () {

    const cardTitleTextColor = useColorModeValue('orange.700', 'yellow.50');
    const cardBodyTextColor = useColorModeValue('orange.500', 'white');
    const cardBackgroundColor = useColorModeValue('yellow.50', 'rgb(67,1,0)')
    const cardBorderColor = useColorModeValue('orange.400', 'rgb(131,0,0)')
    const hoverColor = useColorModeValue('rgb(255, 110, 17)', 'rgb(221,67,67)')
    const cardBoxShadow = useColorModeValue('0px 1px 1px rgb(233,65,45)', '0px 1px 5px rgba(131,0,0,1)')
    const { isOpen, onOpen, onClose } = useDisclosure();


    return (
        <SimpleGrid  spacing={10}  minChildWidth={250}>
            {products && products.map((product, index) => {
               return  (
                   <Card key={product.id} transition={"0.3s"} onClick={onOpen} shadow={cardBoxShadow} _hover={{cursor: 'pointer',boxShadow: `0px 2px 25px ${hoverColor}`}} borderTop="8px" bg={cardBackgroundColor} borderColor={cardBorderColor}>
                       <ProductView showAddToCartBT = {true} key={product.id} isOpen={isOpen} onClose={onClose} product={product} />
                        <CardHeader color={cardTitleTextColor}>
                            <Flex gap={5}>

                                <Box>
                                    <Heading as="h3" size="sm">
                                        {product.title}
                                    </Heading>
                                    <Text>By {product.seller}</Text>
                                </Box>
                            </Flex>
                        </CardHeader>
                       <Divider></Divider>
                       <CardBody color={cardBodyTextColor}>
                        <Text>
                            {product.description}
                        </Text>


                       </CardBody>

                       <Divider colorScheme={"orange"} borderWidth={1} maxWidth="88%" alignSelf={"center"}/>
                       <CardFooter>
                            <HStack>
                                <Button key={product.id} colorScheme={"orange"} variant="outline" onClick={onOpen} leftIcon={<ViewIcon/>} >
                                    View
                                </Button>
                                <Button colorScheme={"yellow"}  variant="ghost" leftIcon={<EditIcon/>}>
                                    Comment
                                </Button>
                            </HStack>
                       </CardFooter>

                   </Card>
               )
            }
            )}
        </SimpleGrid>
    )
}