// ShoppingCart.js
import {
    Button,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    useDisclosure,
    Flex, Image, Text, VStack, useColorModeValue
} from '@chakra-ui/react';
import {FaShoppingCart} from "react-icons/fa";
import React from "react";
import {DeleteIcon} from "@chakra-ui/icons";
import ProductView from "./ProductView";

const ShoppingCart = () => {

    const { isOpen: isCartOpen, onOpen: onCartOpen, onClose: onCartClose } = useDisclosure();
    const { isOpen: isProductViewOpen, onOpen: onProductViewOpen, onClose: onProductViewtClose } = useDisclosure();

    const cartItemBorderBottomColor = useColorModeValue('rgb(67,0,0)', 'orange')
    const cartItemBorderBoxShadow = useColorModeValue('0px 1px 2px rgb(67,0,0)', '0px 1px 5px rgb(211,194,0)')
    let shoppingCartContent = [
        {
            id: 1,
            title: "TV",
            price: "$140.00",
            thumbnail: "data:image/jpeg;base64,",
        },
        {
            id: 2,
            title: "Gaming PC",
            price: "$999.00",
            thumbnail: "data:image/jpeg;base64,"
        }, {
            id: 3,
            title: "Smart Watch",
            price: "$45.00",
            thumbnail: 'data:image/jpeg;base64,'
        }
    ]
    // shoppingCartContent = []
    const cartContentContainerOutShadow = useColorModeValue('0px 2px 30px rgb(67,0,0)', '0px 2px 10px rgb(211,194,0)')

    const cartModalBGColor = useColorModeValue('yellow.50', 'rgb(67,0,0)')
    const cartTextColor = useColorModeValue('rgb(67,0,0)','orange.100')

   // const cartContentContainerInShadow = useColorModeValue('0px 2px 10px -5px rgb(67,0,0) inset', '0px 2px 10px -10px rgb(211,194,0) inset')
    const productImageMixBlendMode = useColorModeValue('multiply', 'blend')

    return (
        <>
            <Button title={"View Shopping Cart"} onClick={onCartOpen} colorScheme="orange" variant="ghost"><FaShoppingCart /></Button>

            <Modal isOpen={isCartOpen} size={"xl"} onClose={onCartClose} allowPinchZoom={true} blockScrollOnMount={false} colorScheme={"orange"} isCentered={true} position={"fixed"} top={"0px"}>
                <ModalOverlay />
                <ModalContent shadow={cartContentContainerOutShadow}   borderRadius = {15} bg={cartModalBGColor} color={cartTextColor}>
                    <ModalHeader>Shopping Cart</ModalHeader>
                    <ModalCloseButton />
                    <ModalBody>
                        <Flex  borderRadius={15}
                              p={15}
                            direction={"column"} pt={4}  pb={4} gap={4} maxHeight={300} overflow={"scroll"} align={'center'}>
                            {shoppingCartContent && shoppingCartContent.map((item) => {
                                return (
                                    <Flex align={"center"} key={item.id} p={4} shadow={cartItemBorderBoxShadow}  borderBottom={`1px solid ${cartItemBorderBottomColor}`} borderStyle={"outset"} borderRadius={'14px'} direction={"row"} width={'90%'} justify={"space-between"}>
                                        <Image _hover={{cursor: 'pointer'}}
                                               onClick={onProductViewOpen}
                                               mixBlendMode={productImageMixBlendMode}
                                               borderRadius={"14px"} src={item.thumbnail} alt={item.title} fit={"cover"} height={100} width={100}/>
                                        <ProductView onClose={onProductViewtClose}
                                        isOpen={isProductViewOpen} product={{
                                            images:[],
                                            title: 'sample product',
                                            description: 'sample description',

                                        }}></ProductView>
                                        <VStack>
                                            <Text>Name: {item.title}</Text>
                                            <Text>Price: {item.price}</Text>
                                        </VStack>
                                        <Button variant={"ghost"} colorScheme={"red"} leftIcon={<DeleteIcon/>}>Remove</Button>
                                    </Flex>
                                )
                            })}
                            {(shoppingCartContent.length === 0 || !shoppingCartContent) && <Text align={"center"} p={10}>Your cart is empty!</Text>}
                        </Flex>

                    </ModalBody>
                    <ModalFooter>
                        <Button colorScheme="orange" mr={3} onClick={onCartClose}>
                            Close
                        </Button>
                        <Button variant="ghost" colorScheme="orange">Checkout</Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>
        </>
    );
};

export default ShoppingCart;
